import React,{useState} from 'react';
import postList from './Components/postList';
import Posts from './Posts';
import AddPost from './Components/AddPost';
import './App.css';
function App() {
  const [posts,setPosts] = useState(postList)
  const [newPost,setnewPost] =useState({
    id:0,
    text:"",
    likes:0
  })

  const updatePost=(id,like)=>{
    let post = posts[id]
    post.likes = like + 1
    let updpost = [...posts]
    updpost[id] = post
    setPosts(updpost)
    // console.log(updpost)
    // console.log(post)
  }

  const createNewPost =(text)=>{
    let id = posts.length
    setnewPost({
      id:id+1,
      text:text,
      likes:0
    })
    // setPosts([...posts,newPost])
  }
  // console.log(newPost)
  const addNewPostToPostList = (text)=>{
    createNewPost(text)
    let nP = [...posts,newPost]
    setPosts(nP)
    console.log(nP)
  }

  return(
    <div>

        {posts.map(({id,text,likes},index)=>
            <Posts key={index} text={text} likes={likes} id ={id} updatePost={(id,like)=>updatePost(id,like)}/>
        )}

       <AddPost addPost={text=>{addNewPostToPostList(text)}}/>
     </div>
  )


}

export default App;
