import React,{useState} from 'react';
import './App.css';
import ProductList from './Components/ProductList'
function App() {
  const [productQ, setproductQ] = useState(productList)
  const incrementQuantity = (index)=>{
    let newQ = [...productQ]
    newQ[index].quantity++
    setproductQ(newQ)
  }
  const productList=[
    {
      id:1,
      price:52000 ,
      model:"Iphone Xs",
      quantity:0
    },
    {
      id:2,
      price:32000 ,
      model:"Iphone Xr",
      quantity:0
    }
  ]
  return (
    <div className="App">
      <ProductList productList={productList} incrementQuantity={incrementQuantity} />
    </div>
  );
}

export default App;
