import React,{useEffect, useState} from 'react';
import { unstable_batchedUpdates } from 'react-dom';
import './App.css';
function App() {
  const postList = [
    {
      id:0,
      text:"This is First Post",
      likes:300
    },
    {
      id:1,
      text:"This is the Second Post",
      likes:200
    },
    {
      id:2,
      text:"This is Third Post",
      likes:500
    }
  ]
  const [posts,setPosts] = useState(postList)
  const [singlePost,setsinglePost] = useState(postList[0])
  /*console.log(posts)*/

  const liked = (post)=>{
      let p = postList[post]
      let like = postList[post].likes + 1
      setsinglePost({...p,likes:like})
      updatePostList(post,singlePost) 
  }

  const updatePostList =(post,singlePost)=>{
    let newPost = [...postList]
    newPost[post] = singlePost
    console.log(newPost[post])
    setPosts(newPost)
    console.log(posts)
  }
  return(
    <div>{
      posts.map((post,i)=>
        <div key={i}>
          <h3>{post.text}</h3>

        </div>
      )
    }

    </div>
  )

}

export default App;
