import React,{useState} from 'react';
import './App.css';
function App() {
  const posts = [
    {
      id:1,
      text:"This is First Post",
      likes:300
    },
    {
      id:2,
      text:"This is the Second Post",
      likes:200
    }
  ]
 const [p,setp] = useState(posts)

 const likes = (index)=>{
   let update =[...posts,likes=likes+1]
   update[index].likes++
   console.log(update[index])
   setp(update)
 }


 const display =(
   <div>
   {posts.map((post)=>
   <>
    <p key={post.id}>
      {post.text}
    <h6 key={post.id}>{post.likes}</h6></p>
    <button onClick={()=>{likes(post.id)}}>likes</button>
  </>
   )}
   </div>
 );

 return(
   <>
    {display}
   </>
 )
}

export default App;
