import React,{useState} from 'react';

function AddPost({posts}) {
    const [p,setP] = useState(posts)
    const [text,setText] = useState("")
    const [newP,setnewP] = useState({
        id:p.length,
        text:"",
        likes:0
    })
    console.log(newP)
    console.log(p)
    const addPost = (text)=>{
        newP.text = text
        let nP = [...p,newP]
        setP(nP)
        // setnewP({
        //     id:nP.length ,
        //     text:"",
        //     likes:0
        // })
    }
  return <div>
      <div>
        <label>Post-Title: </label>
        <input value={text} placeholder='enter post title...' onChange={(e)=>setText(e.target.value)}></input>
      </div>
      <button onClick={()=>{addPost(text)}}>Add Post</button>
  </div>
}

export default AddPost;
