import React,{useState} from 'react';
import postList from './Components/postList';
import Posts from './Posts';
import './App.css';
function App() {
  const [posts,setPosts] = useState(postList)

  const updatePost=(id)=>{
    let post = posts[id]
    console.log(post)
  }

  return(
    <div>
      {posts.map(({id,text,likes},index)=>
          <Posts key={index} text={text} likes={likes} updatePost={id=>updatePost(id)}/>
      )

    }</div>
  ) 

}

export default App;
