import React,{useState} from 'react';


function Posts({id,text,likes,updatePost}) {
    const [like,setLike] = useState(likes)
    const liked=()=>{
        setLike(like+1)
    }

  return <div>
    <h3>{text}</h3>
    <button onClick={()=>{liked();updatePost(id)}}>{like}</button>  
  </div>;
}

export default Posts;
