import React,{useState} from 'react';
import './App.css';
function App() {
  const posts = [
    {
      id:1,
      text:"This is First Post",
      likes:300
    },
    {
      id:2,
      text:"This is the Second Post",
      likes:200
    }
  ]
 const [p,setp] = useState(posts)
 const [like,setLike] =useState([posts.map((post)=>post.likes)])
 console.log(like)

 const likes = (index)=>{
   let update =[...posts]
   let singleUpdate = update[index+1]
   console.log(singleUpdate)
   setp(update)
 }


 const display =(
   <div>
   {posts.map((post)=>
   <>
    <p key={post.id}>
      {post.text}
      <button onClick={()=>{likes(post.id)}}>{post.likes}</button>
    </p>

  </>
   )}
   </div>
 );

 return(
   <>
    {display}
   </>
 )
}

export default App;
