import React,{useState} from 'react';
import { unstable_batchedUpdates } from 'react-dom';
import './App.css';
function App() {
  const posts = [
    {
      id:1,
      text:"This is First Post",
      likes:300
    },
    {
      id:2,
      text:"This is the Second Post",
      likes:200
    }
  ]
 const [p,setp] = useState(posts)
 const [like,setLike] = useState([posts.map((post)=>post.likes)])
 const [newp,setnewp] = useState([{
   id:0,
   text:"",
   likes:0
 }])
 const newpost = (posts)=>{
   setnewp([{
    id:posts.length,
    text:"This is a new post",
    likes:0
   }])

 }
console.log(newp)
 const likes = (index)=>{
  let l = [...like]
  l[0][index]++
  setLike([...l])
  console.log(l)
 }


 const display =(
   <div>
   {posts.map((post)=>
   <div>
    <p key={post.id}>
      {post.id}.{post.text}<br></br>
      <button onClick={()=>{likes(post.id - 1)}}>{like[0][post.id-1]}</button>
    </p>

  </div>
   )}
   </div>
 );

 return(
   <>
    {display}
    <button onClick={()=>{newpost(posts)}}>Add Post</button>
   </>
 )
}

export default App;
