import { unstable_batchedUpdates } from 'react-dom';
import './App.css';
function App() {
  const posts = [
    {
      id:1,
      text:"This is First Post",
      likes:300
    },
    {
      id:2,
      text:"This is the Second Post",
      likes:200
    }
  ]
 const [p,setp] = useState(posts)
 const [like,setLike] =useState([posts.map((post)=>post.likes)])
 /*console.log(like)
 console.log(like[0])
 console.log(like[0][1])*/

 const likes = (index)=>{
  let l = [...like]
  console.log(l)
  l[0][index]++
  setLike([...l])
 }


 const display =(
   <div>
   {posts.map((post)=>
   <>
    <p key={post.id}>
      {post.text}
      <button onClick={()=>{likes(post.id - 1)}}>{like[0][post.id-1]}</button>
    </p>

  </>
   )}
   </div>
 );

 return(
   <>
    {display}
   </>
 )
}

export default App;
