import React,{useEffect, useState} from 'react';
import { unstable_batchedUpdates } from 'react-dom';
import './App.css';
function App() {
  const postList = [
    {
      id:0,
      text:"This is First Post",
      likes:300
    },
    {
      id:1,
      text:"This is the Second Post",
      likes:200
    },
    {
      id:2,
      text:"This is Third Post",
      likes:500
    }
  ]
  const [posts,setPosts] = useState(postList)
  const [singlePost,setsinglePost] = useState({})

  const liked = (post)=>{
    let p = posts[post]
    p.likes++
    setsinglePost(p)
    updatePostList(post)
  }

  const addPost = ()=>{
    setsinglePost(
      {
        id:posts.length+1,
        text:"This is the new Post..",
        likes:0
      }
    )
    addPostList()
  }
  const updatePostList = (post)=>{
    let newPost = [...posts]
    console.log(newPost[post])
    console.log(posts)
    setPosts(newPost)
  }

  const addPostList = ()=>{
    let newPost = [...posts,singlePost]
    console.log(newPost)
  }

  return(
    <div>{
      posts.map((post,i)=>
        <div key={i}>
          <h3>{post.text}</h3>
          <button onClick={()=>liked(post.id)}>{post.likes}</button>       
        </div>
      )
    }
    <button onClick={()=>addPost()}>Add Post</button>
    </div>
  ) 

}

export default App;
